mapper.sql:
where variable_name = #{variableName}
 and day_sign = #{daySign}
 and save_time = #{saveTime}
 and min_time = #{minTime}

 and min_time >= #{params.beginTime}
 and min_time <= #{params.endTime}

mapper.update:
update sharding_month
variable_name = #{variableName}, day_sign = #{daySign}, min_value = #{minValue}, min_time = #{minTime}, max_value = #{maxValue}, max_time = #{maxTime}, avg_value = #{avgValue}, accu_sign = #{accuSign}, accu_value = #{accuValue}, total_count = #{totalCount}, save_time = #{saveTime},
where id = #{id}

mapper.select:
select variable_name, save_time, ROUND(data_value * #{coefficient}, 2) AS data_value, YEAR (save_time) AS year, MONTH (save_time) AS month, DAY (save_time) AS day, HOUR (save_time) AS hour, MINUTE (save_time) AS minute, DATE_FORMAT(save_time, '%Y-%m-%d %H:%i') AS formattedDatetime from sharding_day where variable_name = #{variableName} and save_time = #{saveTime} and save_time >= #{params.beginTime} and save_time <= #{params.endTime}
and MINUTE(save_time) % #{params.minute} = 0
and MINUTE(save_time) = 0 order by formattedDatetime asc

SELECT ROUND(SUM(accu_value) * #{coefficient}, 1)
FROM sharding_month
WHERE variable_name = #{variableName}
    AND min_time >= #{params.beginTime}
    AND min_time <= #{params.endTime}
GROUP BY YEAR(min_time)
