mapper.select:

where delete_flag = 0

	AND menu_model = #{menuModel}


	AND menu_name like concat('%', #{menuName}, '%')


	AND visible = #{visible}


	AND is_admin_visit = #{isAdminVisit}


	AND stop_flag = #{stopFlag}

order by parent_id, order_num

select distinct m.id, m.parent_id, m.menu_name, m.path, m.request_url, m.request_query, m.visible, m.stop_flag, ifnull(m.permission_sign,'') as permission_sign, m.open_type, m.is_cache, m.menu_type, m.icon, m.order_num, m.is_admin_visit, m.create_time from sys_common_menu m where m.menu_type in ('M', 'C') and m.stop_flag = 0 and m.delete_flag = 0
AND m.menu_model = #{menuModel}
order by m.parent_id, m.order_num

		select distinct m.id, m.parent_id, m.menu_name, m.path, m.request_url, m.request_query, m.visible, m.stop_flag, ifnull(m.permission_sign,'') as permission_sign, m.open_type, m.is_cache, m.menu_type, m.icon, m.order_num, m.is_admin_visit, m.create_time
		from sys_common_menu m
		left join sys_role_menu rm on m.id = rm.menu_id
		left join sys_user_role ur on rm.role_id = ur.role_id
		left join sys_role ro on ur.role_id = ro.id
		where m.delete_flag = 0 and ur.user_id = #{params.userId}

			AND m.menu_model = #{menuModel}


            AND m.menu_name like concat('%', #{menuName}, '%')


            AND m.visible = #{visible}


            AND m.stop_flag = #{stopFlag}

		order by m.parent_id, m.order_num

select distinct m.id, m.parent_id, m.menu_name, m.path, m.request_url, m.request_query, m.visible, m.stop_flag, ifnull(m.permission_sign,'') as permission_sign, m.open_type, m.is_cache, m.menu_type, m.icon, m.order_num, m.is_admin_visit, m.create_time
from sys_common_menu m
join sys_enterprise_menu em on em.ent_id = #{params.entId} and em.menu_id = m.id
left join sys_role_menu rm on m.id = rm.menu_id
left join sys_user_role ur on rm.role_id = ur.role_id
left join sys_role ro on ur.role_id = ro.id
where ur.user_id = #{params.userId}

	AND m.menu_model = #{menuModel}


	AND m.menu_name like concat('%', #{menuName}, '%')


	AND m.visible = #{visible}


	AND m.stop_flag = #{stopFlag}

order by m.parent_id, m.order_num

select distinct m.id, m.parent_id, m.menu_name, m.path, m.request_url, m.request_query, m.visible, m.stop_flag, ifnull(m.permission_sign,'') as permission_sign, m.open_type, m.is_cache, m.menu_type, m.icon, m.order_num, m.is_admin_visit, m.create_time from sys_common_menu m left join sys_role_menu rm on m.id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.id left join sys_user u on ur.user_id = u.id where u.id = #{userId} and m.menu_type in ('M', 'C') and m.stop_flag = 0 and m.delete_flag = 0 and ro.stop_flag = 0
AND m.menu_model = #{menuModel}
order by m.parent_id, m.order_num

select m.id from sys_common_menu m left join sys_role_menu rm on m.id = rm.menu_id where m.delete_flag = 0 and rm.role_id = #{roleId}
and m.id not in (select m.parent_id from sys_common_menu m inner join sys_role_menu rm on m.id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

select m.id from sys_common_menu m join sys_enterprise_menu em on em.ent_id = #{entId} and em.menu_id = m.id left join sys_role_menu rm on m.id = rm.menu_id where rm.role_id = #{roleId}
and m.id not in (select m.parent_id from sys_common_menu m inner join sys_role_menu rm on m.id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

mapper.update:
update sys_common_menu
menu_model = #{menuModel}, menu_name = #{menuName}, parent_id = #{parentId}, menu_type = #{menuType}, path = #{path}, request_url = #{requestUrl}, request_query = #{requestQuery}, permission_sign = #{permissionSign}, open_type = #{openType}, is_cache = #{isCache}, icon = #{icon}, visible = #{visible}, order_num = #{orderNum}, is_admin_visit = #{isAdminVisit}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, stop_flag = #{stopFlag}, delete_flag = #{deleteFlag}, update_time = sysdate()
where id = #{id}
