mapper.select:
select u.id, u.ent_id, u.dept_id, u.nick_name, u.logon_name, u.email, u.head_pic, u.mobile, u.is_supper, u.sex, u.stop_flag, u.delete_flag, u.last_login_ip, u.last_login_time, u.pwd_update_date, u.create_by, u.create_time,
d.dept_name, d.leader
from sys_user u
	left join sys_dept d on u.dept_id = d.id
where u.delete_flag = 0

	AND u.id = #{id}


	AND u.ent_id = #{entId}


	AND u.logon_name like concat('%', #{logonName}, '%')


	AND u.nick_name like concat('%', #{nickName}, '%')


	AND u.is_supper = #{isSupper}


	AND u.stop_flag = #{stopFlag}


	AND u.mobile like concat('%', #{mobile}, '%')



	AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')


	AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')



	AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))


	AND u.id in

		#{userId}



${params.dataScope}

select distinct u.id, u.dept_id, u.logon_name, u.nick_name, u.email, u.mobile, u.stop_flag, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.id left join sys_user_role ur on u.id = ur.user_id left join sys_role r on r.id = ur.role_id where u.delete_flag = 0 and r.id = #{roleId}
AND u.logon_name like concat('%', #{logonName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.mobile like concat('%', #{mobile}, '%')
${params.dataScope}

mapper.update:
update sys_user
ent_id = #{entId}, dept_id = #{deptId}, logic_code = #{logicCode}, logon_name = #{logonName}, logon_pwd = #{logonPwd}, nick_name = #{nickName}, mobile = #{mobile}, email = #{email}, post_id = #{postId}, is_supper = #{isSupper}, user_level = #{userLevel}, sex = #{sex}, marry = #{marry}, birthday = #{birthday}, age = #{age}, true_name = #{trueName}, card_type = #{cardType}, card_number = #{cardNumber}, card_pic_front = #{cardPicFront}, card_pic_back = #{cardPicBack}, is_auth = #{isAuth}, country = #{country}, province = #{province}, city = #{city}, county = #{county}, town = #{town}, village = #{village}, detail_address = #{detailAddress}, post_code = #{postCode}, phone = #{phone}, fax = #{fax}, head_pic = #{headPic}, person_des = #{personDes}, reg_time = #{regTime}, reg_ip = #{regIp}, last_login_time = #{lastLoginTime}, last_login_ip = #{lastLoginIp}, login_times = #{loginTimes}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, stop_flag = #{stopFlag}, delete_flag = #{deleteFlag}, update_time = sysdate()
where id = #{id}
